mapper.select:
SELECT BOOKNO,TITLE,AURTHOR,TO_CHAR(REGDATE,'YYYY-MM-DD') REGDATE,KEYWORD,DELFLAG
	FROM MYTOURBOOK
		WHERE BOOKNO= #{bookNo}

 		SELECT BOOKNO,TITLE,AURTHOR,TO_CHAR(REGDATE,'YYYY-MM-DD') REGDATE,KEYWORD,DELFLAG
 			FROM MYTOURBOOK



					REGDATE BETWEEN #{startdate} AND #{enddate}


					REGDATE <= #{enddate}


					REGDATE >= #{startdate}

mapper.update:
 		UPDATE MYTOURBOOK
			SET TITLE=#{title}, AURTHOR=#{aurthor}, KEYWORD=#{keyword}, DELFLAG=#{delflag}
				WHERE BOOKNO=#{bookNo}
